// Must be called with interrupts disabled,
// to prevent race with process being moved
// to a different CPU.
int
cpuid()
{
  int id = r_tp();
  return id;
}

// Return this CPU's cpu struct.
// Interrupts must be disabled.
struct cpu*
mycpu(void) {
  int id = cpuid();
  struct cpu *c = &cpus[id];
  
  return c;
}

// Return the current struct proc *, or zero if none.
struct proc*
myproc(void) {
  push_off();
  struct cpu *c = mycpu();
  struct proc *p = c->proc;
  pop_off();
  return p;
}

int
allocpid() {
  int pid;
  
  acquire(&pid_lock);
  pid = nextpid;
  nextpid = nextpid + 1;
  release(&pid_lock);

  return pid;
}

// Look in the process table for an UNUSED proc.
// If found, initialize state required to run in the kernel,
// and return with p->lock held.
// If there are no free procs, or a memory allocation fails, return 0.
static struct proc*
allocproc(void)
{
  struct proc *p;

  for(p = proc; p < &proc[NPROC]; p++) {
    acquire(&p->lock);
    if(p->state == UNUSED) {
      goto found;
    } else {
      release(&p->lock);
    }
  }
  return NULL;

found:
  p->pid = allocpid();
  p->vma = NULL;
  p->ktime = 1;
  p->utime = 1;
  // Allocate a trapframe page.
  if((p->trapframe = (struct trapframe *)kalloc()) == NULL){
    release(&p->lock);
    return NULL;
  }

  // An empty user page table.
  // And an identical kernel page table for this proc.
  if ((p->pagetable = proc_pagetable(p)) == NULL ||
      (p->kpagetable = proc_kpagetable()) == NULL) {
    freeproc(p);
    release(&p->lock);
    return NULL;
  }

  p->kstack = VKSTACK;

  // Set up new context to start executing at forkret,
  // which returns to user space.
  memset(&p->context, 0, sizeof(p->context));
  p->context.ra = (uint64)forkret;
  p->context.sp = p->kstack + PGSIZE;

  return p;
}

// free a proc structure and the data hanging from it,
// including user pages.
// p->lock must be held.
static void
freeproc(struct proc *p)
{
  if(p->trapframe)
    kfree((void*)p->trapframe);
  p->trapframe = 0;
  if (p->kpagetable) {
    kvmfree(p->kpagetable, 1);
  }
  p->kpagetable = 0;
  if(p->pagetable)
    proc_freepagetable(p->pagetable, p->sz);
  p->pagetable = 0;
  p->vma = NULL;
  p->sz = 0;
  p->pid = 0;
  p->parent = 0;
  p->name[0] = 0;
  p->chan = 0;
  p->killed = 0;
  p->xstate = 0;
  p->state = UNUSED;
}

struct vma *vma_init(struct proc *p)
{
    if (NULL == p) {
        printf("p is not existing\n");
        return NULL;
    }
    struct vma *vma = (struct vma*)kalloc();
    if (NULL == vma) {
        printf("vma kalloc failed\n");
        return NULL;
    }

    vma->type = NONE;
    vma->prev = vma->next = vma;
    p->vma = vma;
    
    if (NULL == alloc_mmap_vma(p,0,USER_MMAP_START,0,0,0,0)) {
        //free_vma_list(p);
        return NULL;
    }

    return vma;
}

struct vma* alloc_vma(struct proc *p,enum segtype type,uint64 addr, uint64 sz,int perm,int alloc,uint64 pa) {
    uint64 start = PGROUNDDOWN(addr);
    uint64 end = addr + sz;
    end = PGROUNDUP(end);

    struct vma* find_vma = p->vma->next;
    while (find_vma != p->vma) {
        if (end <= find_vma->addr) 
            break;
        else if(start >= find_vma->end)
            find_vma = find_vma->next;
        else {
            printf("vma address overflow\n");
            return NULL;
        }
    }
    struct vma* vma = (struct vma*)kalloc();
    if (NULL == vma) {
        printf("vma kalloc failed\n");
        return NULL;
    }
    if (0 != sz) {
        if (alloc) {
            if (0 != uvmalloc1(p->pagetable,start,end,perm)) {
                printf("uvmalloc failed\n");
                kfree(vma);
                return NULL;
            }
        } else if (pa != 0) {
            if (0 != mappages(p->pagetable,start,sz,pa,perm)) {
                printf("mappages failed\n");
                kfree(vma);
                return NULL;
            }
        }
    }
    vma->addr = start;
    vma->sz = sz;
    vma->perm = perm;
    vma->end = end;
    vma->fd = -1;
    vma->f_off = 0;
    vma->type = type;
    vma->prev = find_vma->prev;
    vma->next = find_vma;
    find_vma->prev->next = vma;
    find_vma->prev = vma;
    
    return vma;
}

struct vma* find_mmap_vma(struct vma* head) 
{
    struct vma* vma = head ->next;
    while (vma != head) {
        if (MMAP == vma->type)
            return vma;
        vma = vma->next;
    }
    return NULL;
}

struct vma* alloc_mmap_vma(struct proc *p, int flags, uint64 addr, uint64 sz, int perm, int fd ,uint64 f_off)
{
    struct vma* vma = NULL;
    struct vma* find_vma = find_mmap_vma(p->vma);
    if (0 == addr) 
        addr = PGROUNDDOWN(find_vma->addr - sz);
    vma = alloc_vma(p,MMAP,addr,sz,perm,1,NULL);
    if (NULL == vma) 
    {
        printf("alloc_mmap_vma: alloc_vma failed\n");
        return NULL;
    }
    vma->fd = fd;
    vma->f_off = f_off;
    
    return vma;
}
// Create a user page table for a given process,
// with no user memory, but with trampoline pages.
pagetable_t
proc_pagetable(struct proc *p)
{
  pagetable_t pagetable;

  // An empty page table.
  pagetable = uvmcreate();
  if(pagetable == 0)
    return NULL;

  if (NULL == vma_init(p)) {
    uvmfree(pagetable, 0);
    return NULL;
  }
  // map the trampoline code (for system call return)
  // at the highest user virtual address.
  // only the supervisor uses it, on the way
  // to/from user space, so not PTE_U.
  if(mappages(pagetable, TRAMPOLINE, PGSIZE,
              (uint64)trampoline, PTE_R | PTE_X) < 0){
    uvmfree(pagetable, 0);
    return NULL;
  }

  // map the trapframe just below TRAMPOLINE, for trampoline.S.
  if(mappages(pagetable, TRAPFRAME, PGSIZE,
              (uint64)(p->trapframe), PTE_R | PTE_W) < 0){
    vmunmap(pagetable, TRAMPOLINE, 1, 0);
    uvmfree(pagetable, 0);
    return NULL;
  }

  return pagetable;
}

// Free a process's page table, and free the
// physical memory it refers to.
void
proc_freepagetable(pagetable_t pagetable, uint64 sz)
{
  vmunmap(pagetable, TRAMPOLINE, 1, 0);
  vmunmap(pagetable, TRAPFRAME, 1, 0);
  uvmfree(pagetable, sz);
}

